import React, { useState, useRef, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import Menubar from '../sections/Menubar.js';

const mapContainerStyle = {
  height: '600px',
  width: '100%'
};

const center = {
  lat: -34.397,
  lng: 150.644
};

const libraries = ['places'];

const MapComponent = ({ title }) => {
  const [mapCenter, setMapCenter] = useState(center);
  const [chargingStations, setChargingStations] = useState([]);
  const mapRef = useRef(null);
  const searchBoxRef = useRef(null);

  const onLoad = (ref) => {
    searchBoxRef.current = ref;
  };

  const onMapLoad = (map) => {
    mapRef.current = map;
    searchNearby(map, mapCenter);
  };

  const onPlacesChanged = () => {
    const places = searchBoxRef.current.getPlaces();
    if (places && places.length > 0) {
      const location = places[0].geometry.location;
      const newCenter = { lat: location.lat(), lng: location.lng() };
      setMapCenter(newCenter);
      searchNearby(mapRef.current, newCenter);
    }
  };

  const searchNearby = (map, center) => {
    const service = new window.google.maps.places.PlacesService(map);
    service.nearbySearch(
      {
        location: center,
        radius: '5000', // Search within 5km radius
        type: ['charging_station'], // Search for EV charging stations
      },
      (results, status) => {
        if (status === window.google.maps.places.PlacesServiceStatus.OK && results) {
          setChargingStations(results);
        }
      }
    );
  };

  useEffect(() => {
    if (mapRef.current && mapCenter) {
      searchNearby(mapRef.current, mapCenter);
    }
  }, [mapCenter]);

  return (
    <div>
      <Menubar />
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '20px' }}>
        <h2>{title}</h2>
        <LoadScript
          googleMapsApiKey="AIzaSyChSvk8z0j12Y0wxtUe3gFPRiCTKqBrYtI"
          libraries={libraries}
        >
          <StandaloneSearchBox
            onLoad={onLoad}
            onPlacesChanged={onPlacesChanged}
          >
            {/* Search box input */}
          </StandaloneSearchBox>
          <GoogleMap
            mapContainerStyle={mapContainerStyle}
            center={mapCenter}
            zoom={10}
            onLoad={onMapLoad}
          >
            {chargingStations.map((station) => (
              <Marker
                key={station.place_id}
                position={{
                  lat: station.geometry.location.lat(),
                  lng: station.geometry.location.lng(),
                }}
                icon={{
                  url: "http://maps.google.com/mapfiles/ms/icons/red-dot.png",
                }}
              />
            ))}
          </GoogleMap>
        </LoadScript>
      </div>
    </div>
  );
};

export default MapComponent;
