import React, { useState, useEffect, useRef } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import Menubar from '../sections/Menubar.js';

const mapContainerStyle = {
  height: '600px',
  width: '100%'
};

const center = {
  lat: -34.397,
  lng: 150.644
};

const libraries = ["places"];

const MapComponent = ({ title }) => {
  const [mapCenter, setMapCenter] = useState(center);
  const [chargingStations, setChargingStations] = useState([]);
  const mapRef = useRef();

  const onMapLoad = (map) => {
    mapRef.current = map;
    fetchEVChargingStations(map);
  };

  const fetchEVChargingStations = (map) => {
    const service = new window.google.maps.places.PlacesService(map);
    const request = {
      location: mapCenter,
      radius: '5000',
      type: ['charging_station']
    };

    service.nearbySearch(request, (results, status) => {
      if (status === window.google.maps.places.PlacesServiceStatus.OK && results) {
        setChargingStations(results);
      }
    });
  };

  useEffect(() => {
    if (mapRef.current) {
      fetchEVChargingStations(mapRef.current);
    }
  }, [mapCenter]);

  return (
    <div>
      <Menubar />
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '20px' }}>
        <h2>{title}</h2>
        <LoadScript
          googleMapsApiKey="AIzaSyChSvk8z0j12Y0wxtUe3gFPRiCTKqBrYtI"
          libraries={libraries}
        >
          <GoogleMap
            mapContainerStyle={mapContainerStyle}
            center={mapCenter}
            zoom={10}
            onLoad={onMapLoad}
          >
            {chargingStations.map(station => (
              <Marker
                key={station.place_id}
                position={{
                  lat: station.geometry.location.lat(),
                  lng: station.geometry.location.lng(),
                }}
              />
            ))}
          </GoogleMap>
        </LoadScript>
      </div>
    </div>
  );
};

export default MapComponent;
