import React, { useState, useRef } from 'react';
import { GoogleMap, LoadScript, StandaloneSearchBox } from '@react-google-maps/api';
import Menubar from '../sections/Menubar.js';

// Improved styles
const mapContainerStyle = {
  height: '600px', // Increased map height
  width: '100%' // Map width to take full container width
};

const center = {
  lat: -34.397,
  lng: 150.644
};

const MapComponent = ({ title }) => {
  const [mapCenter, setMapCenter] = useState(center);
  const searchBoxRef = useRef(null);

  const onLoad = (ref) => {
    searchBoxRef.current = ref;
  };

  const onPlacesChanged = () => {
    const places = searchBoxRef.current.getPlaces();
    if (places && places.length > 0) {
      const location = places[0].geometry.location;
      setMapCenter({
        lat: location.lat(),
        lng: location.lng()
      });
    }
  };

  return (
    <div>
      <Menubar />
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '20px' }}>
        <h2>{title}</h2>
        <LoadScript
          googleMapsApiKey="AIzaSyChSvk8z0j12Y0wxtUe3gFPRiCTKqBrYtI"
          libraries={['places']}
        >
          <div style={{ marginBottom: '20px', width: '100%', display: 'flex', justifyContent: 'center' }}>
            <StandaloneSearchBox
              onLoad={onLoad}
              onPlacesChanged={onPlacesChanged}
            >
              <input
                type="text"
                placeholder="Search for places..."
                style={{
                  boxSizing: `border-box`,
                  border: `1px solid #ccc`,
                  width: `300px`, // Increased width
                  height: `40px`, // Increased height
                  padding: `0 12px`,
                  borderRadius: `5px`,
                  boxShadow: `0 2px 6px rgba(0, 0, 0, 0.2)`,
                  fontSize: `16px`, // Increased font size
                  outline: `none`,
                  textOverflow: `ellipses`,
                }}
              />
            </StandaloneSearchBox>
          </div>
          <GoogleMap
            mapContainerStyle={mapContainerStyle}
            center={mapCenter}
            zoom={10}
          >
            {/* Additional Map components like markers can be added here */}
          </GoogleMap>
        </LoadScript>
      </div>
    </div>
  );
};

export default MapComponent;
