import React, { useState, useRef, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, StandaloneSearchBox } from '@react-google-maps/api';
import Menubar from '../sections/Menubar.js';

const libraries = ["places"];

const MapComponent = ({ title }) => {
  const [mapCenter, setMapCenter] = useState(center);
  const [chargingStations, setChargingStations] = useState([]);
  const mapRef = useRef(null);
  const searchBoxRef = useRef(null);

  const onMapLoad = map => {
    mapRef.current = map;
  };

  const onLoadSearchBox = ref => {
    searchBoxRef.current = ref;
  };

  const onPlacesChanged = () => {
    const places = searchBoxRef.current.getPlaces();
    if (places && places.length) {
      const location = places[0].geometry.location;
      setMapCenter({
        lat: location.lat(),
        lng: location.lng()
      });
      // Optionally, trigger search for charging stations here
    }
  };

  return (
    <div>
      <Menubar />
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '20px' }}>
        <h2>{title}</h2>
        <LoadScript googleMapsApiKey="AIzaSyChSvk8z0j12Y0wxtUe3gFPRiCTKqBrYtI" libraries={libraries}>
          <StandaloneSearchBox onLoad={onLoadSearchBox} onPlacesChanged={onPlacesChanged}>
            <input
              type="text"
              placeholder="Search for places..."
              style={{
                boxSizing: `border-box`,
                border: `1px solid transparent`,
                width: `240px`,
                height: `32px`,
                padding: `0 12px`,
                borderRadius: `3px`,
                boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
                fontSize: `14px`,
                outline: `none`,
                textOverflow: `ellipses`,
                marginTop: "10px",
              }}
            />
          </StandaloneSearchBox>
          <GoogleMap
            mapContainerStyle={mapContainerStyle}
            center={mapCenter}
            zoom={10}
            onLoad={onMapLoad}
          >
            {/* Markers for charging stations */}
            {chargingStations.map(station => (
              <Marker
                key={station.place_id}
                position={{
                  lat: station.geometry.location.lat(),
                  lng: station.geometry.location.lng(),
                }}
                icon={{
                  url: "http://maps.google.com/mapfiles/ms/icons/red-dot.png",
                }}
              />
            ))}
          </GoogleMap>
        </LoadScript>
      </div>
    </div>
  );
};
